// Este código extrae el contenido mejorado del título de la respuesta de OpenAI
// y lo formatea como un objeto JSON con la propiedad 'improvedTitle'

// Accedemos a los datos de entrada (la respuesta de OpenAI)
const openAiResponse = $input.first();

// Intentamos acceder al contenido mejorado en diferentes estructuras posibles
let improvedTitle = "No se pudo procesar el título mejorado";

// Verificamos la estructura que has mostrado (array con mensaje en [0])
if (Array.isArray(openAiResponse) && openAiResponse[0] && openAiResponse[0].message && openAiResponse[0].message.content) {
  // Estructura que estás recibiendo actualmente
  improvedTitle = openAiResponse[0].message.content;
} else if (openAiResponse && openAiResponse.choices && openAiResponse.choices[0] && openAiResponse.choices[0].message) {
  // Estructura estándar de OpenAI
  improvedTitle = openAiResponse.choices[0].message.content;
} else if (openAiResponse && openAiResponse.message && openAiResponse.message.content) {
  // Estructura alternativa (si n8n ya ha procesado la respuesta)
  improvedTitle = openAiResponse.message.content;
} else if (openAiResponse && openAiResponse.content) {
  // Otra estructura posible
  improvedTitle = openAiResponse.content;
} else if (typeof openAiResponse === 'string') {
  // Si la respuesta es directamente un string
  improvedTitle = openAiResponse;
}

// Devolvemos un objeto JSON con la estructura esperada
return {
  json: {
    improvedTitle: improvedTitle
  }
};
